    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        var boxvalue = jComboBox1.getSelectedItem().toString();
        if (boxvalue == "Penjumlahan") {
            penjumlahan();
        } else if (boxvalue == "Pengurangan") {
            pengurangan();
        } else {
            perkalian();
        }

    }//GEN-LAST:event_jButton2ActionPerformed

    private void perkalian() {
        C00.setText(parsePerkalian(A00.getText(), B00.getText(),
                A01.getText(), B10.getText(),
                A02.getText(), B20.getText()));
        C01.setText(parsePerkalian(A00.getText(), B01.getText(),
                A01.getText(), B11.getText(),
                A02.getText(), B21.getText()));
        C02.setText(parsePerkalian(A00.getText(), B02.getText(),
                A01.getText(), B12.getText(),
                A02.getText(), B22.getText()));

        C10.setText(parsePerkalian(A10.getText(), B00.getText(),
                A11.getText(), B10.getText(),
                A12.getText(), B20.getText()));
        C11.setText(parsePerkalian(A10.getText(), B01.getText(),
                A11.getText(), B11.getText(),
                A12.getText(), B21.getText()));
        C12.setText(parsePerkalian(A10.getText(), B02.getText(),
                A11.getText(), B12.getText(),
                A12.getText(), B22.getText()));

        C20.setText(parsePerkalian(A20.getText(), B00.getText(),
                A21.getText(), B10.getText(),
                A22.getText(), B20.getText()));
        C21.setText(parsePerkalian(A20.getText(), B01.getText(),
                A21.getText(), B11.getText(),
                A22.getText(), B21.getText()));
        C22.setText(parsePerkalian(A20.getText(), B02.getText(),
                A21.getText(), B12.getText(),
                A22.getText(), B22.getText()));
    }

    private String parsePerkalian(String val1, String val2, String val3, String val4, String val5, String val6) {
        int value1 = Integer.parseInt(val1);
        int value2 = Integer.parseInt(val2);
        int value3 = Integer.parseInt(val3);
        int value4 = Integer.parseInt(val4);
        int value5 = Integer.parseInt(val5);
        int value6 = Integer.parseInt(val6);

        int kali1 = value1 * value2;
        int kali2 = value3 * value4;
        int kali3 = value5 * value6;

        return String.valueOf(kali1 + kali2 + kali3);
    }

