    private void pengurangan() {
        C00.setText(kurangkan(A00.getText(), B00.getText()));
        C01.setText(kurangkan(A01.getText(), B01.getText()));
        C02.setText(kurangkan(A02.getText(), B02.getText()));

        C10.setText(kurangkan(A10.getText(), B10.getText()));
        C11.setText(kurangkan(A11.getText(), B11.getText()));
        C12.setText(kurangkan(A12.getText(), B12.getText()));

        C20.setText(kurangkan(A20.getText(), B20.getText()));
        C21.setText(kurangkan(A21.getText(), B21.getText()));
        C22.setText(kurangkan(A22.getText(), B22.getText()));
    }

    private String kurangkan(String val1, String val2) {
        return String.valueOf(Integer.parseInt(val1) - Integer.parseInt(val2));
    }

    private void penjumlahan() {
        C00.setText(jumlahkan(A00.getText(), B00.getText()));
        C01.setText(jumlahkan(A01.getText(), B01.getText()));
        C02.setText(jumlahkan(A02.getText(), B02.getText()));

        C10.setText(jumlahkan(A10.getText(), B10.getText()));
        C11.setText(jumlahkan(A11.getText(), B11.getText()));
        C12.setText(jumlahkan(A12.getText(), B12.getText()));

        C20.setText(jumlahkan(A20.getText(), B20.getText()));
        C21.setText(jumlahkan(A21.getText(), B21.getText()));
        C22.setText(jumlahkan(A22.getText(), B22.getText()));
    }

    String jumlahkan(String value1, String value2) {
        int val1 = Integer.parseInt(value1);
        int val2 = Integer.parseInt(value2);
        return String.valueOf(val1 + val2);
    }

